import React, { useState } from "react";
import MonacoEditor from "react-monaco-editor";
import axios from "axios";
import { BonusFeatures } from "./components/BonusFeatures";
import { codeHints } from "./utils/codeHints";
import "./styles/main.css";
import "./styles/bonus.css";

function App() {
  const [code, setCode] = useState("");
  const [output, setOutput] = useState("");
  const [language, setLanguage] = useState("javascript");
  const [theme, setTheme] = useState("vs-dark");
  const [isLoading, setIsLoading] = useState(false);

  const editorOptions = {
    fontSize: 16,
    minimap: { enabled: true },
    autoClosingBrackets: true,
    formatOnPaste: true,
    suggestOnTriggerCharacters: true,
  };

  const handleCodeChange = (newCode) => {
    setCode(newCode);
  };

  const handleLanguageChange = (event) => {
    setLanguage(event.target.value);
  };

  const handleThemeChange = (newTheme) => {
    setTheme(newTheme);
  };

  const formatCode = () => {
    const editor = window.monaco.editor.getModels()[0];
    editor.getAction("editor.action.formatDocument").run();
  };

  const getHint = () => {
    const hints = codeHints[language];
    const hintKeys = Object.keys(hints);
    const randomHint =
      hints[hintKeys[Math.floor(Math.random() * hintKeys.length)]];
    setOutput(`Hint: ${randomHint}`);
  };

  const executeCode = async () => {
    setIsLoading(true);
    try {
      const response = await axios.post("http://localhost:5000/code/execute", {
        code,
        language,
      });
      setOutput(response.data.output);
    } catch (error) {
      setOutput(error.response?.data?.output || "Error executing code");
    }
    setIsLoading(false);
  };

  return (
    <div className="app-container">
      <div className="main-content">
        <div className="language-selector-container">
          <select
            className="language-selector"
            value={language}
            onChange={handleLanguageChange}
          >
            <option value="javascript">JavaScript</option>
            <option value="python">Python</option>
            <option value="java">Java</option>
          </select>
        </div>

        <div className="editor-container">
          <MonacoEditor
            width="100%"
            height="500px"
            language={language}
            theme={theme}
            value={code}
            options={editorOptions}
            onChange={handleCodeChange}
          />
        </div>

        <div className="control-panel">
          <button
            className="execute-btn"
            onClick={executeCode}
            disabled={isLoading}
          >
            {isLoading ? "Executing..." : "Run Code"}
          </button>
        </div>

        <div className="output-panel">
          <h3>Output:</h3>
          <pre>{output}</pre>
        </div>
      </div>

      <BonusFeatures
        onHint={getHint}
        onFormat={formatCode}
        onThemeChange={handleThemeChange}
      />
    </div>
  );
}

export default App;
