import React, { useState } from 'react';
import CodeMirror from '@uiw/react-codemirror';
import { javascript } from '@codemirror/lang-javascript';
import { python } from '@codemirror/lang-python';
import { java } from '@codemirror/lang-java';
import { dracula } from '@uiw/codemirror-theme-dracula';
import { githubDark } from '@uiw/codemirror-theme-github';
import { vscodeDark } from '@uiw/codemirror-theme-vscode';
import ErrorBoundary from './components/ErrorBoundary';
import { saveCode } from './utils/api';
import './App.css';

function App() {
  const [code, setCode] = useState('// Write your code here');
  const [language, setLanguage] = useState('javascript');
  const [theme, setTheme] = useState('vscodeDark');
  const [isFullScreen, setIsFullScreen] = useState(false);
  const [font, setFont] = useState('default');

  const themes = {
    dracula: dracula,
    githubDark: githubDark,
    vscodeDark: vscodeDark,
  };

  const languages = {
    javascript: javascript(),
    python: python(),
    java: java(),
  };

  const handleCodeChange = (value) => {
    setCode(value);
  };

  const handleLanguageChange = (e) => {
    setLanguage(e.target.value);
  };

  const handleThemeChange = (e) => {
    setTheme(e.target.value);
  };

  const handleFontChange = (e) => {
    setFont(e.target.value);
  };

  const toggleFullScreen = () => {
    setIsFullScreen(!isFullScreen);
  };

  const handleSave = async () => {
    try {
      const data = await saveCode(code, language);
      alert(`Code saved successfully! ID: ${data.id}`);
    } catch (error) {
      alert('Error saving code');
    }
  };

  return (
    <ErrorBoundary>
      <div className={`App ${isFullScreen ? 'fullscreen' : ''}`}>
        <h1>Online Code Editor</h1>
        <div className="editor-controls">
          <select value={language} onChange={handleLanguageChange}>
            <option value="javascript">JavaScript</option>
            <option value="python">Python</option>
            <option value="java">Java</option>
          </select>
          <select value={theme} onChange={handleThemeChange}>
            <option value="vscodeDark">VS Code Dark</option>
            <option value="dracula">Dracula</option>
            <option value="githubDark">GitHub Dark</option>
          </select>
          <select value={font} onChange={handleFontChange}>
            <option value="default">Default</option>
            <option value="fira-code">Fira Code</option>
            <option value="jetbrains-mono">JetBrains Mono</option>
            <option value="source-code-pro">Source Code Pro</option>
          </select>
          <button onClick={toggleFullScreen}>
            {isFullScreen ? 'Exit Fullscreen' : 'Fullscreen'}
          </button>
        </div>
        <div className="editor-container">
          <CodeMirror
            value={code}
            height={isFullScreen ? '90vh' : '400px'}
            theme={themes[theme]}
            extensions={[languages[language]]}
            onChange={handleCodeChange}
            className={`editor-font-${font}`}
          />
          <button onClick={handleSave}>Save Code</button>
        </div>
      </div>
    </ErrorBoundary>
  );
}

export default App;
